"use strict";(self["webpackChunkbeyond_books_vue"]=self["webpackChunkbeyond_books_vue"]||[]).push([[9151],{9151:n=>{n.exports='{\n    "terms": {\n        "银月寺": {\n            "color": "银月寺",\n            "description": "一座综合城镇，异能者保留地，里面有大学，也有风力发电机和风车。"\n        },\n        "异能者": {\n            "color": "异能者",\n            "description": "异能者在生命的某一刻觉醒特定异能。异能者一般都有冰层一样的灵能盾（异能盾）来保护自己。异能者主要从环境中吸收灵力，其自身也会不断产生灵力。"\n        },\n        "保留地": {\n            "color": "保留地",\n            "description": "在2011年，柳国颁布《异能者保留地法》，在全国各地秘密建立保留地，不对公众公开，也禁止公众公开传播保留地信息。柳国要求异能者居住在保留地内，并尽量给保留地配齐生活设施等，满足保留地的物质需求和精神需求。柳国对于保留地内的异能者加以保护和关心。异能者在保留地就像在家里一样。"\n        },\n        "飞雁市": {\n            "color": "飞雁市",\n            "description": "一座人类的靠海城市，并非异能者保留地，靠近海洋的部分大楼林立，但其他部分发展速度不快。"\n        },\n        "银月大学": {\n            "color": "银月寺",\n            "description": "银月寺中的异能者大学，接受来自银月寺的异能者作为学生，学生也在银月寺内保就业。全称银月次灵大学。"\n        },\n        "罗伯特": {\n            "color": "罗伯特",\n            "description": "Robert是银月寺的保安，异能为【苍穹缝隙】。",\n            "imageUrl": "/portrait/robert.png"\n        },\n        "贝利斯": {\n            "color": "贝利斯",\n            "description": "一个强大的异能者雇佣兵组织，其新任领导是田中连，他上任后就奔赴各地索取保管能量之体。"\n        },\n        "罪恶猎手": {\n            "color": "罪恶猎手",\n            "description": "罪恶猎手是在保留地周围挑衅并把异能者们赶回保留地（银月寺）的反异能者组织。"\n        },\n        "合法持枪": {\n            "color": "罪恶猎手",\n            "description": "柳国禁枪，但是罪恶猎手们可以持有一些。"\n        },\n        "田中连": {\n            "color": "田中连",\n            "description": "Tolinia是贝利斯集团的新任领导。男性。",\n            "imageUrl": "/portrait/tanaka_ren.png"\n        },\n        "艾琳": {\n            "color": "艾琳",\n            "description": "Irene是银月寺内的银月大学的新生，当然也是异能者。",\n            "imageUrl": "/portrait/eileen.png"\n        },\n        "【折射】": {\n            "color": "折射",\n            "description": "银月寺持有的一种能量之体。"\n        },\n        "【星辰】": {\n            "color": "克莱门特",\n            "description": "望穿秋水湖底的一种能量之体。是受到丰沛灵力滋润的小石子。"\n        },\n        "能量之体": {\n            "color": "能量之体",\n            "description": "蕴含巨大的能量，一般来说会有类似异能的能力。"\n        },\n        "卡斯帕": {\n            "color": "卡斯帕",\n            "description": "Kaspar是银月大学的棍术老师。",\n            "imageUrl": "/portrait/caspar.png"\n        },\n        "史蒂文": {\n            "color": "史蒂文",\n            "description": "Steve是银月寺的主持。"\n        },\n        "珂茵": {\n            "color": "珂茵",\n            "description": "Corn是银月寺的保安，具有用光弓射出光箭并瞬移到光箭位置的异能。",\n            "imageUrl": "/portrait/karin.png"\n        },\n        "诺顿": {\n            "color": "诺顿",\n            "description": "Norton是银月寺的保安，未觉醒异能，但也有灵能盾，棍术也很厉害。",\n            "imageUrl": "/portrait/norton.png"\n        },\n        "辛妮亚": {\n            "color": "辛妮亚",\n            "description": "Xinea是银月寺的新任保安，珂茵的闺蜜，具有使周围不能传播声音的异能。",\n            "imageUrl": "/portrait/cynthia.png"\n        },\n        "图尔斯": {\n            "color": "图尔斯",\n            "description": "Tours是贝利斯雇佣兵，罗伯特的初中同学，异能为【芯铁消逝】。",\n            "imageUrl": "/portrait/tours.png"\n        },\n        "【苍穹缝隙】": {\n            "color": "苍穹缝隙",\n            "description": "罗伯特的异能，可以使时间流速变慢，而自身的速度不变。"\n        },\n        "【芯铁消逝】": {\n            "color": "芯铁消逝",\n            "description": "图尔斯的异能，可以生成和消逝钢铁。"\n        },\n        "斯图尔特": {\n            "color": "斯图尔特",\n            "description": "Stewart是贝利斯雇佣兵，异能是【内能构建】。他背上嵌入了钢铁羽翼。",\n            "imageUrl": "/portrait/stuart.png"\n        },\n        "克莱门特": {\n            "color": "克莱门特",\n            "description": "Clement是贝利斯雇佣兵，异能是【衔羽展翼】。武器是长锤。",\n            "imageUrl": "/portrait/clement.png"\n        },\n        "琼": {\n            "color": "琼",\n            "description": "Joan使用生成冰的能力。",\n            "imageUrl": "/portrait/joan.png"\n        },\n        "杰瑞斯": {\n            "color": "罪恶猎手",\n            "description": "Jerry是罪恶猎手高官，异能是【潜龙无影】，武器是匕首。",\n            "imageUrl": "/portrait/jayce.png"\n        },\n        "【内能构建】": {\n            "color": "内能构建",\n            "description": "斯图尔特的异能，可以生成各种形式的火焰。"\n        },\n        "【衔羽展翼】": {\n            "color": "衔羽展翼",\n            "description": "克莱门特的异能，可以在背后长出羽翼来飞行。"\n        },\n        "【冰雪风暴】": {\n            "color": "冰雪风暴",\n            "description": "琼所释放的冰雪风暴，使周围落下大雪，冰冻一切，大量消耗环境中的灵力。"\n        },\n        "【破晓行动】": {\n            "color": "破晓行动",\n            "description": "贝利斯入侵银月寺，企图夺取能量之体【折射】的一次行动。"\n        },\n        "【清扫行动】": {\n            "color": "破晓行动",\n            "description": "贝利斯入侵银月寺，企图夺取能量之体【折射】的第二次行动。"\n        },\n        "【音域净空】": {\n            "color": "辛妮亚",\n            "description": "辛妮亚的异能，可以阻断周围音波的传播。"\n        },\n        "异能溢出": {\n            "color": "异能者",\n            "description": "异能溢出是刚觉醒异能时异能不受控制的现象。"\n        }\n        ,"【在回家之前到家】": {\n            "color": "罗伯特",\n            "description": "罗伯特的异能溢出事件，发生在2008年罗伯特读高中的时候。具体请看：<a href=\'https://tobenot.github.io/2024/06/28/Story-Arrive-Before-Go-Home/\' target=\'_blank\'>小说《在回家之前到家》</a>"\n        }\n        ,"【鲸歌海铭】": {\n            "color": "艾琳",\n            "description": "艾琳声称自己持有的异能，据她说可以操控水。"\n        }\n        ,"【潜龙无影】": {\n            "color": "罪恶猎手",\n            "description": "杰瑞斯的异能，可以让自己隐身。"\n        }\n        ,"灵能盾": {\n            "color": "能量之体",\n            "description": "异能者不需要觉醒异能就有灵能盾，受到攻击身体会如冰层一般裂开，而减轻身体的受伤，灵能盾是被动的，会消耗灵力随着时间恢复。"\n        }\n        ,"异能盾": {\n            "color": "能量之体",\n            "description": "同“灵能盾”，不同的叫法而已。"\n        }\n        ,"望穿秋水": {\n            "color": "罗伯特",\n            "description": "银月寺西边森林中的一处瀑布，瀑布下有一个湖。"\n        }\n        ,"默默等待": {\n            "color": "默默等待",\n            "description": "作者注：不沟通而只静默守候，这并不是合适的社交行为，请勿模仿。"\n        }\n    }\n}\n'}}]);